import React, { useState } from "react";
import "./ActivateUser.css";

const Slider = () => {
  const [isConfirmFadeOut, setIsConfrimFadeOut] = useState(false);
  const [isDeleteFadeOut, setIsDeleteFadeOut] = useState(true);

  $("#confirm").on("change", function () {
    var slidepos = $(this).val();
    if (slidepos > 99) {
      // User slided the slider
      $("#confirm").fadeOut();
      $(".delete-notice").fadeIn();
    }
  });

  const handleChange = () => {};

  return (
    <div class="confirmation-slider">
      <div id="status">
        <input
          id="confirm"
          type="range"
          value="0"
          min="0"
          max="100"
          onChange={handleChange}
          className={isConfirmFadeOut ? "fadeIn" : "fadeOut"}
        />
        <span
          class="delete-notice"
          className={isConfirmFadeOut ? "fadeIn" : "fadeOut"}
        >
          File deleted.
        </span>
      </div>
    </div>
  );
};

export default Slider;
