import React, { useState } from "react";
import "./ActivateUser.css";

const Slider = () => {
  const [isConfirmFadeOut, setIsConfrimFadeOut] = useState(false);
  const [isDeleteFadeOut, setIsDeleteFadeOut] = useState(true);

  const handleChange = () => {
    setIsConfrimFadeOut(true);
    setIsDeleteFadeOut(false);
  };

  return (
    <div class="confirmation-slider">
      <div id="status">
        <input
          id="confirm"
          type="range"
          value="0"
          min="0"
          max="100"
          onChange={handleChange}
          className={isConfirmFadeOut ? "fadeIn" : "fadeOut"}
        />
        <span
          class="delete-notice"
          className={isDeleteFadeOut ? "fadeIn" : "fadeOut"}
        >
          Activated
        </span>
      </div>
    </div>
  );
};

export default Slider;
