import React, { useState } from "react";
import "./Apartaments.css";
import { library } from "@fortawesome/fontawesome-svg-core";
import {
  faSearch,
  faPlus,
  faMinus,
  faTimes,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./SearchBar.css";
const SearchBar = (props) => {
  const [districtsList, setDistrictsList] = useState([
    "Bielany",
    "Wola",
    "Żoliborz",
    "Mokotów",
  ]);

  const [rooms, setRooms] = useState(0);
  const [area, setArea] = useState({
    min: "",
    max: "",
  });
  const [price, setPrice] = useState({
    min: "",
    max: "",
  });
  const [pricePerM, setPricePerM] = useState({
    min: "",
    max: "",
  });
  const [description, setDesctiption] = useState("");
  const [district, setDistrict] = useState("");

  library.add(faSearch, faPlus, faMinus, faTimes);

  const handleChangeDescription = (enteredDescription) => {
    setDesctiption(enteredDescription);
  };

  return (
    <div className="search-bar">
      <div className="search-description">
        <h5>Description:</h5>

        <input
          value={description}
          onChange={(e) => {
            handleChangeDescription(e.target.value);
          }}
        ></input>
        <div
          className="close-icon"
          onClick={() => {
            setDesctiption("");
          }}
        >
          <FontAwesomeIcon icon={["fa", "times"]} size="xs" color="#dddddd" />
        </div>
      </div>

      <div className="search-description">
        <h5>Rooms:</h5>
        <div
          className="plus"
          onClick={() => {
            setRooms(rooms - 1);
          }}
        >
          <FontAwesomeIcon icon={["fa", "minus"]} size="xs" color="#dddddd" />
        </div>
        <span value={rooms}>{rooms}</span>

        <div
          className="plus"
          onClick={() => {
            setRooms(rooms + 1);
          }}
        >
          <FontAwesomeIcon icon={["fa", "plus"]} size="xs" color="#dddddd" />
        </div>
        <div
          className="close-icon"
          onClick={() => {
            setRooms(0);
          }}
        >
          <FontAwesomeIcon icon={["fa", "times"]} size="xs" color="#dddddd" />
        </div>
      </div>

      <div className="search-description">
        <h5>Area:</h5>
        <input
          value={area.min}
          className="area-input"
          onChange={(e) => {
            setArea({ ...area, min: e.target.value });
          }}
        ></input>
        <h5>m² - </h5>
        <input
          value={area.max}
          className="area-input"
          onChange={(e) => setArea({ ...area, max: e.target.value })}
        ></input>
        <h5>m²</h5>
        <div
          className="close-icon"
          onClick={() => {
            setArea({ min: "", max: "" });
          }}
        >
          <FontAwesomeIcon icon={["fa", "times"]} size="xs" color="#dddddd" />
        </div>
      </div>
      <div className="search-description">
        <h5>Price:</h5>
        <input
          value={price.min}
          className="price-input"
          onChange={(e) => setPrice({ ...price, min: e.target.value })}
        ></input>
        <h5>zł - </h5>
        <input
          className="price-input"
          value={price.max}
          onChange={(e) => setPrice({ ...price, max: e.target.value })}
        ></input>
        <h5>zł</h5>
        <div
          className="close-icon"
          onClick={() => {
            setPrice({ min: "", max: "" });
          }}
        >
          <FontAwesomeIcon icon={["fa", "times"]} size="xs" color="#dddddd" />
        </div>
      </div>
      <div className="search-description">
        <h5>Price per m:</h5>
        <input
          value={price.min}
          className="price-input"
          onChange={(e) => setPricePerM({ ...price, min: e.target.value })}
        ></input>
        <h5>zł - </h5>
        <input
          className="price-input"
          value={price.max}
          onChange={(e) => setPricePerM({ ...price, max: e.target.value })}
        ></input>
        <h5>zł</h5>
        <div
          className="close-icon"
          onClick={() => {
            setPrice({ min: "", max: "" });
          }}
        >
          <FontAwesomeIcon icon={["fa", "times"]} size="xs" color="#dddddd" />
        </div>
      </div>

      <div>
        <label for="">Choose a car:</label>

        <select id="cars" name="cars">
          <option value="volvo">Volvo</option>
          <option value="saab">Saab</option>
          <option value="fiat">Fiat</option>
          <option value="audi">Audi</option>
        </select>
      </div>
      <div
        className="search-button"
        onClick={() => props.handleClickSearch(description, area, price)}
      >
        <FontAwesomeIcon icon={["fa", "search"]} size="xs" color="white" />
      </div>
    </div>
  );
};
export default SearchBar;
